import React, { Component } from 'react'

export default class Exchange extends Component {
    state = {
        amount1: 1,
        amount2: 0,
        c1: "USD",
        c2: "CNY",
    }
    handleChange = (e) => {
        this.setState({
            amount1: e.target.value
        })
    }

    setCurrency1=(e)=> {
        this.setState({
            c1: e.target.value
        })
    }
    setCurrency2=(e)=> {
        this.setState({
            c2: e.target.value
        })
    }
    async componentDidMount(){
            console.log("aaa");

            let response = await fetch(`https://api.exchangerate-api.com/v4/latest/${this.state.c1}`);
            let data = await response.json()
            const rate = data.rates[this.state.c2];
            console.log(rate);

            this.setState({
                amount2: rate
            })

    }
    render() {
        return (

            <div>
                {this.state.c1}
                {this.state.c2}
                <select onChange={this.setCurrency1} id="currency-one">
                    <option value="USD">USD</option>
                    <option value="CNY">CNY</option>
                    <option value="EUR">EUR</option>

                </select>
                <input type="number" placeholder="number" value={this.state.amount1} onChange={this.handleChange}/>

                <div className="swap-rate-container">
                    <button className="btn" id="swap">
                        Swap
                    </button>
                    <div className="rate" id="rate"></div>
                </div>
                <select onChange={this.setCurrency2} id="currency-one">
                    <option value="CNY">CNY</option>
                    <option value="USD">USD</option>
                    <option value="EUR">EUR</option>

                </select>
                {this.state.amount1},
                {this.state.amount2}

                <input type="number" placeholder="0" value={this.state.amount2} />


            </div>
        )
    }
}
